   let new_butterfly = {
        "butterfly_name": this.state.butterfly_name,
        "scientific_name": this.state.scientific_name,
        "image_file_name": this.state.image_file_name,
        "description": this.state.description
   };
   let data = JSON.stringify(new_butterfly);
   console.log(data);
    let post = await axios.post("http://localhost:3001/api/v1/butterflies", new_butterfly)
      .catch(error => {
        if(error.response) {
          console.log(error.response);
        }
      });
*/
    ajax({
        url: "http://localhost:3001/api/v1/butterflies",
        type: "POST",
        data: { butterfly: { 
          butterfly_name: this.state.butterfly_name,
          scientific_name: this.state.scientific_name,
          image_file_name: this.state.image_file_name,
          description: this.state.description
        }}
      })
    }

  render() {

    return (
      <form className="add-form">
        <label>
          Add Butterfly:
        </label>
          <br/>
            <label>
            Butterfly name:
            </label>
              <input
                  className="name"
                  type="string"
                  name="butterfly_name"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
            <label>
            Scientific name:
            </label>
              <input
                  className="name"
                  type="string"
                  name="scientific_name"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
            <label>
            <br/>
            Attach Image:
            </label>
              <input
                  className="name"
                  type="string"
                  name="image_file_name"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
            <br/>
            <label>
            Description
            </label>
              <input
                  className="name"
                  type="text"
                  name="description"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
              <br/>
              <button  
                className="submit-button"
                onClick={this.addItem}>
                Submit
                </button>
      </form>

    );
  }
}

export default AddForm;
