import React, {Component} from 'react'
import './App.css';
import axios from 'axios';
import Query from './Query.js';


class SearchForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
        query_city:'',
        areas:[]
    };
    this.updateQuery = this.updateQuery.bind(this);
    this.fetchQueryResults = this.fetchQueryResults.bind(this);

console.log("constructor");  }

  updateQuery(event) {
      this.setState({
        query_city: event.target.value.toUpperCase()
      });
    }

  fetchQueryResults(event) {
    event.preventDefault();
    let search = "https://localhost:3001" + this.state.query_city;
      axios.get(search)
      .then(result => {
          this.setState({areas:result.data});
        })
      //  console.log(this.state.areas);
    }

  render() {

    let result = this.state.areas.map((properties) =>
        <Query data={properties} />
    );

    return (
      <form>
        <span>
        <label>
          Search:
            <input
                className="search"
                type="text"
                value={this.state.value}
                onChange={this.updateQuery}
              />
        </label>
              <br />
              <button onClick={this.fetchQueryResults}>Search</button>
        </span>
              {result}
      </form>

    );
  }
}

export default SearchForm;
