import React, {Component} from 'react'
import './App.css';
//import axios from 'axios';



class AddForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
        butterfly_name:'',
        scientific_name:'',
        image_file_name:'',
        description:''
    };
    this.updateItem = this.updateItem.bind(this);
    this.addItem = this.addItem.bind(this);
  }

// updates component's object key-value pairs
  updateItem(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

console.log(name);
console.log(value);
      this.setState({
          [name]: value
      });
    }

  async addItem(event) {
    event.preventDefault();

   let data = {
        butterfly_name: this.state.butterfly_name,
        scientific_name: this.state.scientific_name,
        image_file_name: this.state.image_file_name,
        description: this.state.description
   };
   console.log(data);
    let post = await axios.post("http://localhost:3001/api/v1/butterflies", data);

    }

  render() {

    return (
      <form className="add-form">
        <label>
          Add Butterfly:
        </label>
          <br/>
            <label>
            Butterfly name:
            </label>
              <input
                  className="name"
                  type="string"
                  name="butterfly_name"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
            <label>
            Scientific name:
            </label>
              <input
                  className="name"
                  type="string"
                  name="scientific_name"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
            <label>
            <br/>
            Attach Image:
            </label>
              <input
                  className="name"
                  type="string"
                  name="image_file_name"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
            <br/>
            <label>
            Description
            </label>
              <input
                  className="name"
                  type="text"
                  name="description"
                  value={this.state.value}
                  onChange={this.updateItem}
                />
              <br/>
              <button  
                className="submit-button"
                onClick={this.addItem}>
                Submit
                </button>
      </form>

    );
  }
}

export default AddForm;
