import React, {Component} from 'react'
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import axios from 'axios';


class AddForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
        query:'',
        result:[]
    };
    this.updateQuery = this.updateQuery.bind(this);
    this.fetchQueryResults = this.fetchQueryResults.bind(this);
  }

  updateQuery(event) {
      this.setState({
        query: event.target.value.toUpperCase()
      });
    }

  fetchQueryResults(event) {
    event.preventDefault();
    let search = "https://localhost:3001/" + this.state.query;
      axios.get(search)
      .then(result => {
          this.setState({result:result.data});
        })
    }

  render() {

    /*
    let result = this.state.result.map((properties) =>
        <Query data={properties} />
    );

    */

    /** test object 
    let obj = {
        butterfly_name: "monarch",
        scientific_name: "mon",
        image_file_name: SOF,
        description: "yellow, black, white"
    }

    let result = <Query data={obj}/>

    **/

    return (
      <form>
        <span>
        <label>
          Search:
            <input
                className="search"
                type="text"
                value={this.state.value}
                onChange={this.updateQuery}
              />
        </label>
              <button  
                className="search-button"
                onClick={this.fetchQueryResults}>
                Search
                </button>
        </span>
      </form>

    );
  }
}

export default AddForm;
