import React, {Component} from 'react'
import './App.css';
import axios from 'axios';
import Query from './Query.js';


class SearchForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
        query:'',
        results:[]
    };
    this.updateQuery = this.updateQuery.bind(this);
    this.fetchQueryResults = this.fetchQueryResults.bind(this);
  }

  updateQuery(event) {
      this.setState({
        query: event.target.value.toUpperCase()
      });
        console.log(event.target.value);

    }

  fetchQueryResults(event) {
    event.preventDefault();
    console.log(this.state.query)
    let search = "https://localhost:3001/" + this.state.query;
      axios.get(search)
      .then(result => {
        console.log(search);
        //  this.setState({areas:result.data});
        })
    }

  render() {

    let result = this.state.areas.map((properties) =>
        <Query data={properties} />
    );

    return (
      <form>
        <span>
        <label>
          Search:
            <input
                className="search"
                type="text"
                value={this.state.value}
                onChange={this.updateQuery}
              />
        </label>
              <button  
                className="search-button"
                onClick={this.fetchQueryResults}>
                Search
                </button>
        </span>
              {result}
      </form>

    );
  }
}

export default SearchForm;
